var imageUrl;function init(a){imageUrl=a;$("#selected-cards").droppable({accept:".available"});$("#selected-cards li").dblclick(function(b){}).click(function(b){loadPreviewImage(parseInt($(this).attr("id").substr(1),10))});$("#available-cards li").draggable({appendTo:"body",helper:"clone",revert:"invalid"}).dblclick(function(b){}).click(function(b){loadPreviewImage(parseInt($(this).attr("id").substr(1),10))})}function loadPreviewImage(a){$.ajax({url:imageUrl,data:{card:a},type:"POST",dataType:"json",success:function(b){if(b.success){$("#previewImage").attr("src","_game/cards/"+b.image)}else{$("#previewImage").attr("src","_game/cards/cardback.jpg")}}})};