var globals={imageUrl:""};function init(a){globals.imageUrl=a;var b=$("#cards");$("#filterSelected").keyup(function(d){var c=$(this);if(c.val().length){filter(b.find("li"),c.val())}else{b.find("li:hidden").show()}});b.find("li").click(function(c){loadPreviewImage(parseInt($(this).attr("id").substr(1),10))})}function loadPreviewImage(a){$.ajax({url:globals.imageUrl,data:{card:a},type:"POST",dataType:"json",success:function(b){if(b.success){$("#previewImage").attr("src","_game/cards/"+b.image)}else{$("#previewImage").attr("src","_game/cards/cardback.jpg")}}})}function filter(a,b){b=$.trim(b).replace(/ /gi,"|");a.each(function(){($(this).text().search(new RegExp(b,"i"))<0)?$(this).hide():$(this).show()})};